import React, { Component } from 'react';

// import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';

import './pomodoro.css';

const mapStateToProps = (store) => ({
  addTasks: store.tasks.addTasks
});

const mapDispatchToProps = dispatch => ({
  // addTask: (info) => {
  //   dispatch(addTask(info));
  // },
});

class Pomodoro extends Component {

  constructor(props){
    super(props);
    this.state = {
      time: '00:00',
      pomodoro: '1500',
      running: undefined,
      control: undefined,
    };
  }

  handleStart = () => {
    const { pomodoro } = this.state;
    const running = setInterval(function () {
      this.decrement(pomodoro);
    }, 1000);
  }

  handlePause = () => {

  }

  handleReset = () => {

  }

  decrement = () => {
    const { pomodoro } = this.state;

    if(pomodoro > 0) {
      pomodoro = pomodoro -1;
      console.log(pomodoro)
      this.updateDom();
    }
    else if(pomodoro === 0){
      pomodoro = 7;
    }
    else{
      clearInterval(this.state,running);
      alert('5 min de descanço!!');
    }
  }

  renderFormatTime = () => {
    const { pomodoro } = this.state;
    const minutos = Math.floor(pomodoro % 3600 / 60);
    const segundos = Math.floor(pomodoro % 3600 % 60);

    return minutos < 10 ? "0" : '' + minutos + ':' + segundos < 10 ? '0' : '' + segundos
  }

  render() {
    return (
      <div className="Todo">
        <div>
          <span>{this.state.time}</span> 
        </div>
        <div id="buttons">
          <button 
            className="btn"
            onClick={this.handleStart}
          >
          Start
          </button>
          <button
            className="btn"
            onClick={this.handlePause}
          >
          Pause
          </button>
          <button 
            className="btn"
            onClick={this.handleReset}
          >
          Reset
          </button>
        </div>
      </div>
    )
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Pomodoro);
