});

class Pomodoro extends Component {

  constructor(props){
    super(props);
    this.state = {
      time: '00:00',
      pomodoro: '1500',
      control: undefined,
    };
  }

  handleStart() {

  }

  handlePause() {

  }

  handleReset() {

  }

  renderFormatTime = () => {
    const { pomodoro } = this.state;
    const minutos = Math.floor(pomodoro % 3600 / 60);
    const segundos = Math.floor(pomodoro % 3600 % 60);

    return minutos < 10 ? "0" : '' + minutos + ':' + segundos < 10 ? '0' : '' + segundos
  }

  render() {
    return (
      <div className="Todo">
        <div>
          <span>{this.state.time}</span> 
        </div>
        <div id="buttons">
          <button 
            className="btn"
            onClick={this.handleStart}
          >
          Start
          </button>
          <button
            className="btn"
            onClick={this.handlePause}
          >
          Pause
          </button>
          <button 
            className="btn"
            onClick={this.handleReset}
          >
          Reset
          </button>
        </div>
      </div>
    )
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Pomodoro);
